#include "script_component.hpp"

// Handle the ThreeDen Editor Camera
if ((!isNil {is3DEN}) && {is3DEN}) exitWith {true};

if (GVAR(showInThirdPerson)) then {
    cameraView in ["GROUP"] || EFUNC(common,isFeatureCameraActive) 
} else {
    cameraView in ["EXTERNAL", "GROUP"] || EFUNC(common,isFeatureCameraActive) 
};

/*
 * Author: Garth 'L-H' de Wet, commy2
 * Turns 0-1 damage of explosion Event into a rating system of 0-3
 *
 * Arguments:
 * 0: The amount of damage <NUMBER>
 *
 * Return Value:
 * The rating [0-3] <NUMBER>
 *
 * Example:
 * 0.05 call ace_goggles_fnc_getExplosionIndex;
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_damage"];

if (_damage <= 0.04) exitWith {0};
