/*
 * Author: esteldunedain
 * Create one action per passenger.
 *
 * Arguments:
 * 0: Vehicle <OBJECT>
 * 1: Player <OBJECT>
 * 3: Parameters <ARRAY>
 *
 * Return Value:
 * Children actions <ARRAY>
 *
 * Example:
 * [target, player, [params]] call ace_interaction_fnc_addPassengersActions
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_vehicle", "_player"];

private "_actions";
_actions = [];

{
    private _unit = _x;

    if (_unit != _player && {getText (configFile >> "CfgVehicles" >> typeOf _unit >> "simulation") != "UAVPilot"}) then {
        private _icon = [
            "",
            "A3\ui_f\data\IGUI\RscIngameUI\RscUnitInfo\role_driver_ca.paa",
            "A3\ui_f\data\IGUI\RscIngameUI\RscUnitInfo\role_gunner_ca.paa",
            "A3\ui_f\data\IGUI\RscIngameUI\RscUnitInfo\role_commander_ca.paa"
        ] select (([driver _vehicle, gunner _vehicle, commander _vehicle] find _unit) + 1);

        if (_unit getVariable [QEGVAR(captives,isHandcuffed), false]) then {
            _icon = QPATHTOEF(captives,UI\handcuff_ca.paa);
        };

        _actions pushBack [
            [
                format ["%1", _unit],
                [_unit, true] call EFUNC(common,getName),
                _icon,
                { 
                    //statement (Run on hover) - reset the cache so we will insert actions immedietly when hovering over new unit
                    TRACE_2("Cleaning Cache",_target,vehicle _target);
                    [vehicle _target, QEGVAR(interact_menu,ATCache_ACE_SelfActions)] call EFUNC(common,eraseCache);
                },
                {true},
                {
                    if (EGVAR(interact_menu,selectedTarget) isEqualTo _target) then {
                        _this call FUNC(addPassengerActions)
                    } else {
                        [] //not selected, don't waste time on actions
                    };
                },
                [_unit],
                {[0, 0, 0]},
                2,
                [false,false,false,true,false] //add run on hover (4th bit true)
                ] call EFUNC(interact_menu,createAction),
                [],
                _unit
            ];
    };
    false
} count crew _vehicle;

_actions

/*
 * Author: BaerMitUmlaut
 * Checks if unit has a spare magazine for the specified weapon.
 *
 * Arguments:
 * 0: Unit that passes the magazine <OBJECT>
 * 1: Unit to pass the magazine to <OBJECT>
 * 2: Weapon classname <STRING>
 *
 * Return Value:
 * Unit can pass magazine <BOOL>
 *
 * Example:
 * [_player, _target, "arifle_MX_F"] call ace_interaction_fnc_canPassMagazine
 *
 * Public: No
 */

#include "script_component.hpp"
params ["_player", "_target", "_weapon"];

if (!GVAR(enableMagazinePassing)) exitWith {false};
if (((vehicle _target) != _target) && {(vehicle _target) != (vehicle _player)}) exitWith {false};

private _compatibleMags = getArray (configfile >> "CfgWeapons" >> _weapon >> "magazines");
{
    _x params ["_className", "", "_loaded"];
    if ((_className in _compatibleMags) && {!_loaded} && {_target canAdd _className}) exitWith {true};
    false
} foreach (magazinesAmmoFull _player);

/*
 * Author: commy2
 * Find door.
 *
 * Arguments:
 * 0: Distance <NUMBER>
 *
 * Return Value:
 * House objects and door <ARRAY>
 * 0: House <OBJECT>
 * 1: Door Name <STRING>
 *
 * Example:
 * [player, target] call ace_interaction_fnc_getDoor
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_distance"];

private ["_position0", "_position1", "_intersections", "_house", "_door"];

_position0 = positionCameraToWorld [0, 0, 0];
_position1 = positionCameraToWorld [0, 0, _distance];

_intersections = lineIntersectsSurfaces [AGLToASL _position0, AGLToASL _position1, cameraOn, objNull, true, 1, "GEOM"];

if (_intersections isEqualTo []) exitWith {[objNull, ""]};

_house = _intersections select 0 select 2;

// shithouse is bugged
if (typeOf _house == "") exitWith {[objNull, ""]};

_intersections = [_house, "GEOM"] intersect [_position0, _position1];

_door = _intersections select 0 select 0;

if (isNil "_door") exitWith {[_house, ""]};

[_house, _door]

/*
 * Author: KoffeinFlummi, commy2
 * Forces a civilian to the ground with a chance of failure.
 *
 * Arguments:
 * 0: Unit <OBJECT>
 * 1: Target <OBJECT>
 *
 * Return Value:
 * None
 *
 * Example:
 * [civillian] call ace_interaction_fnc_getDown
 *
 * Public: No
 */
#include "script_component.hpp"

#define SEND_RADIUS 10

params ["_unit", "_target"];

_unit playActionNow "GestureGo";

private "_chance";
_chance = [0.5, 0.8] select (count weapons _unit > 0);

{
    if (count weapons _x == 0 && {random 1 < _chance}) then {
        [QGVAR(getDown), [_x], [_x]] call CBA_fnc_targetEvent;
    };
    false
} count (_target nearEntities ["Civilian", SEND_RADIUS]);

/*
 * Author: esteldunedain
 * Return a suitable position for the action point for the given target vehicle
 *
 * Arguments:
 * None (uses local variable _target)
 *
 * Return value:
 * Children actions <ARRAY>
 *
 * Example:
 * call ace_interaction_fnc_getVehiclePos
 *
 * Public: No
 */
#include "script_component.hpp"
// IGNORE_PRIVATE_WARNING(_target);

private _bb = boundingBoxReal _target;
(_bb select 0) params ["_bbX", "_bbY", "_bbZ"];

//Helicopter's rotors distort the bounding box, assume a max of 3 meters width for the body
if (_target isKindOf "Helicopter") then {_bbX = (_bbx min 3) max -3;};

private _relPos = _target worldToModelVisual ASLToAGL EGVAR(interact_menu,cameraPosASL);
#ifdef DEBUG_MODE_FULL
    _relPos = _target worldToModelVisual ASLToAGL eyePos ACE_player;
#endif
_relPos params ["_dx", "_dy", "_dz"];

private _ndx = (abs _dx) / ((abs (_bbx)) - 1);
private _ndy = (abs _dy) / ((abs (_bbY)) - 1);
private _ndz = (abs _dz) / ((abs (_bbZ)) - 1);

private "_pos";
if (_ndx > _ndy) then {
    if (_ndx > _ndz) then {
        // _ndx is greater, will colide with x plane first
        _pos = _relPos vectorMultiply ((1 / _ndx) min 0.8);
    } else {
        // _ndz is greater, will colide with z plane first
        _pos = _relPos vectorMultiply ((1 / _ndz) min 0.8);
    };
} else {
    if (_ndy > _ndz) then {
        // _ndy is greater, will colide with y plane first
        _pos = _relPos vectorMultiply ((1 / _ndy) min 0.8);
    } else {
        // _ndz is greater, will colide with z plane first
        _pos = _relPos vectorMultiply ((1 / _ndz) min 0.8);
    };
};
//Set max height at player's eye level (prevent very high interactin point on choppers)
_pos set [2, (_pos select 2) min _dz];
TRACE_4("",_bb,_bbX,_relPos, _pos);
_pos

///////////////////
