        }] call EFUNC(interact_menu,createAction);

        [_type, 1, ["ACE_SelfActions"], _onAction] call EFUNC(interact_menu,addActionToClass);
        [_type, 1, ["ACE_SelfActions"], _offAction] call EFUNC(interact_menu,addActionToClass);
    };
} forEach allTurrets _vehicle;

/*
 * Author: jaynus
 * Turns off passed laser self designation.
 *
 * Arguments:
 * 0: Shooter, player shooting the laser
 * 1: LaserUUID, the UUID of the laser returned by EFUNC(laser,laserOn)
 * 2: Local laser target, unused.
 *
 * Return Value:
 * True <BOOL>
 */
#include "script_component.hpp"

if( (count _this) > 2) then {
    params ["", "_laserUuid"];
    [_laserUuid] call EFUNC(laser,laserOff);
    // @TODO: Nou gets to field all tickets about missing lasers.
    //deleteVehicle _localLaserTarget;
};

GVAR(active) = false;

if(!isNil QGVAR(selfDesignateHandle)) then {
    [GVAR(selfDesignateHandle)] call CBA_fnc_removePerFrameHandler;
    GVAR(selfDesignateHandle) = nil;
};

true

/*
 * Author: jaynus
 * Turns on laser self designation from this vehicle based on the turret.
 * There are no arguments, because it is all strictly based on the users vehicle.
 *
 * Arguments:
 * None
 *
 * Return Value:
 * None
 *
 * Public: No
 */
#include "script_component.hpp"

TRACE_1("enter", _this);

#define FCS_UPDATE_DELAY 1

FUNC(laserHudDesignatePFH) = {
    private["_strongestResultPos", "_args", "_localLaserTarget", "_laserResultPosition", "_laserResult", "_shooter", "_vehicle", "_weapon", "_gunnerInfo", "_turretInfo", "_pov", "_gunBeg", "_gunEnd", "_povPos", "_povDir", "_result", "_resultPositions", "_firstResult", "_forceUpdateTime"];
    params ["_args"];
    _args params ["_shooter", "_localLaserTarget"];
    _vehicle = vehicle _shooter;
    TRACE_1("", _args);

    if((vehicle _shooter) == _shooter || {!alive _shooter} || {isNull _vehicle} || {!GVAR(active)} ) exitWith {
        _args call FUNC(laserHudDesignateOff);
    };
    if(!([_shooter] call FUNC(unitTurretHasDesignator)) ) exitWith {
        _args call FUNC(laserHudDesignateOff);
    };

    if( (count _args) < 4) then {
        _args set[3, diag_tickTime + FCS_UPDATE_DELAY];
    };
    _forceUpdateTime = _args select 3;

    // @TODO: We don't have anything here we need to do the calculations for right now
    /*

    _gunnerInfo = [_vehicle, (currentWeapon _vehicle)] call CBA_fnc_getFirer;
    _turretInfo = [_vehicle, _gunnerInfo select 1] call EFUNC(common,getTurretDirection);
    _povPos = _turretInfo select 0;

    _laserCode = (vehicle ACE_player) getVariable["ace_laser_code", ACE_DEFAULT_LASER_CODE];
    _waveLength = (vehicle ACE_player) getVariable["ace_laser_waveLength", ACE_DEFAULT_LASER_WAVELENGTH];


    _laserResult = [_povPos, [_waveLength,_waveLength], _laserCode] call EFUNC(laser,seekerFindLaserSpot);
    _laserResultPosition = _laserResult select 0;
    TRACE_1("Search", _laserResult);

    if((count _laserResult) > 0) then {
        // @TODO: Nou gets to field all tickets about missing lasers.
        //_localLaserTarget setPosASL _laserResultPosition;
    };
    */

    if(diag_tickTime > _forceUpdateTime) then {
        ["ace_fcs_forceUpdate", []] call ace_common_fnc_localEvent;
         _args set[3, diag_tickTime + FCS_UPDATE_DELAY];
    };

    _this set[0, _args];
};

private ["_laserTarget", "_handle", "_vehicle", "_laserUuid", "_waveLength", "_beamSpread", "_laserCode"];

if(!GVAR(active)) then {
    GVAR(active) = true;

    TRACE_1("Activating laser", "");

    // Get the self-designation variables, or use defaults
    _laserCode = (vehicle ACE_player) getVariable["ace_laser_code", ACE_DEFAULT_LASER_CODE];
    _waveLength = (vehicle ACE_player) getVariable["ace_laser_waveLength", ACE_DEFAULT_LASER_WAVELENGTH];
    _beamSpread = (vehicle ACE_player) getVariable["ace_laser_beamSpread", ACE_DEFAULT_LASER_BEAMSPREAD];

    _laserUuid = [(vehicle ACE_player), ACE_player, QFUNC(findLaserSource), _waveLength, _laserCode, _beamSpread] call EFUNC(laser,laserOn);

    // @TODO: Create the local target for the players side
    // @TODO: Nou gets to field all tickets about missing lasers.
    //_localLaserTarget = "LaserTargetW" createVehicleLocal (getpos ACE_player);

    GVAR(selfDesignateHandle) = [FUNC(laserHudDesignatePFH), 0.1, [ACE_player, _laserUuid, nil]] call CBA_fnc_addPerFrameHandler;
} else {
    [] call FUNC(laserHudDesignateOff);
    [] call FUNC(laserHudDesignateOn);
};

/*
 * Author: esteldunedain
 * Checks if the turret occupied by the given unit has a laser designator
 *
 * Arguments:
 * 0: Unit <OBJECT>
 *
 * Return Value:
 * Has unit designator <BOOL>
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_unit"];

// Get the player turret path
private ["_turret","_config","_turretConfig"];
_turret = [_unit] call EFUNC(common,getTurretIndex);
_config = configFile >> "CfgVehicles" >> typeOf vehicle _unit;
_turretConfig = [_config, _turret] call EFUNC(common,getTurretConfigPath);

getNumber (_turretConfig >> QGVAR(Enabled)) > 0

laserpointer.text:
/*
 * Author: commy2 and esteldunedain
 * Draw a Laser Point
 *
 * Arguments:
 * 0: Target unit <OBJECT>
 * 1: Range <NUMBER>
 * 2: is Green <BOOL>
 * 3: Brightness <NUMBER>
 *
 * Return Value:
 * None
 *
 * Example:
 * [player, 10, false, 2] call ace_laserpointer_fnc_drawLaserpoint
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_target", "_range", "_isGreen", "_brightness"];

private _unit = ACE_player;

private _p0 = AGLToASL (_target modelToWorldVisual (_target selectionPosition "righthand"));

// Find a system of orthogonal reference vectors
// _v1 points in the direction of the weapon
// _v2 points to the right of the weapon
// _v3 points to the top side of the weapon
private _v1 = _target weaponDirection currentWeapon _target;
private _v2 = vectorNormalized (_v1 vectorCrossProduct [0,0,1]);
private _v3 = _v2 vectorCrossProduct _v1;

// Offset over the 3 reference axis
// This offset could eventually be configured by weapon in the config
#define OFFV1 0.31
#define OFFV2 0
#define OFFV3 0.08

// Offset _p0, the start of the laser
_p0 = _p0 vectorAdd (_v1 vectorMultiply OFFV1) vectorAdd (_v3 vectorMultiply OFFV3) vectorAdd (_v2 vectorMultiply OFFV2);

// Calculate _p1, the potential end of the laser
private _p1 = _p0 vectorAdd (_v1 vectorMultiply _range);

private _pL = lineIntersectsSurfaces [_p0, _p1, _unit, vehicle _unit] select 0 select 0;

// no intersection found, quit
if (isNil "_pL") exitWith {};

private _distance = _p0 vectorDistance _pL;

//systemChat str _distance;
if (_distance < 0.5) exitWith {};

_pL = _p0 vectorAdd (_v1 vectorMultiply _distance);

private _pL2 = _p0 vectorAdd (_v1 vectorMultiply (_distance - 0.5));

_pL = ASLtoAGL _pL;

/*
drawLine3D [
    _p0,
    _pL,
    [[1,0,0,1], [0,1,0,1]] select _isGreen
];
*/

//systemChat str [_target, "FIRE"] intersect [_camPos, _pL];

private _camPos = positionCameraToWorld [0,0,0.2];

if (count ([_target, "FIRE"] intersect [_camPos, _pL]) > 0) exitWith {};
if (count ([_unit, "FIRE"] intersect [_camPos, _pL]) > 0) exitWith {};

// Convert _camPos to ASL
_camPos = AGLToASL _camPos;

if (terrainIntersectASL [_camPos, _pL2]) exitWith {};
if (lineIntersects [_camPos, _pL2]) exitWith {};

private _size = 2 * sqrt (1 / _distance) * (call EFUNC(common,getZoom));

drawIcon3D [
    format ["\a3\weapons_f\acc\data\collimdot_%1_ca.paa", ["red", "green"] select _isGreen],
    [[1,0.25,0.25,0.5*_brightness], [0.25,1,0.25,0.5*_brightness]] select _isGreen,
    _pL,
    _size,
    _size,
    45,
    "",
    0,
    0.05
];

/*
 * Author: commy2
 * Draw the visible laser beams of all cached units.
 *
 * Arguments:
 * None
 *
 * Return Value:
 * None
 *
 * Public: No
 */
#include "script_component.hpp"

private _isIR = currentVisionMode ACE_player;

if (_isIR == 2) exitWith {};

_isIR = _isIR == 1;

private _brightness = 2 - call EFUNC(common,ambientBrightness);

{
    private _weapon = currentWeapon _x;
    private _laser = (_x weaponAccessories _weapon) select 1;

    if (_laser != "") then {
        private _cacheName = format [QGVAR(laser_%1), _laser];
        private _laserID = missionNamespace getVariable [_cacheName, -1];

        if (missionNamespace getVariable [_cacheName, -1] == -1) then {
            _laserID = getNumber (configFile >> "CfgWeapons" >> _laser >> "ACE_laserpointer");
            missionNamespace setVariable [_cacheName, _laserID];
        };

        if (_laserID > 0 && {_x isFlashlightOn _weapon}) then {
            [_x, 100, (_laserID == 2 || _isIR), _brightness] call FUNC(drawLaserpoint);
        };
    };
    false
} count GVAR(nearUnits);

/*
 * Author: Commy2
 * Switch between laser modes.
 *
 * Arguments:
 * 0: Unit <OBJECT>
 * 1: Weapon <STRING>
 *
 * Return Value:
 * None
 *
 * Public: No
 */
#include "script_component.hpp"

params ["_unit", "_weapon"];

private _pointer = (_unit weaponAccessories _weapon) select 1;

if (_pointer == "") exitWith {};

private _config = configFile >> "CfgWeapons" >> _pointer;

private _nextPointer = getText (_config >> "ACE_nextModeClass");

if (_nextPointer == "") exitWith {};

//If system disabled, don't switch to a laser:
private _nextPointerIsLaser = getNumber (configFile >> "CfgWeapons" >> _nextPointer >> "ACE_laserpointer");
if ((!GVAR(enabled)) && {_nextPointerIsLaser == 1}) exitWith {};

// disable inheritance for this entry, because addons claim this as a base class for convenience
if !((_config >> "ACE_nextModeClass") in configProperties [_config, "true", false]) exitWith {};

_unit addWeaponItem [_weapon, _nextPointer];

private _error = false;

if ((_unit weaponAccessories _weapon) select 1 != _nextPointer) then {
    ERROR("NextPointer not compatible");
    _unit addWeaponItem [_weapon, _pointer];
    _error = true;
};

if (!_error) then {
    private _description = getText (configFile >> "CfgWeapons" >> _nextPointer >> "ACE_modeDescription");
    private _picture = getText (configFile >> "CfgWeapons" >> _nextPointer >> "picture");

    [_description, _picture] call EFUNC(common,displayTextPicture);
} else {
    ACE_LOGERROR_3("Failed to add %1 to %2 - reverting to %3",_nextPointer,_weapon,_pointer);
};

playSound "ACE_Sound_Click";

logistics_uavbattery.text:
/*
 * Author: marc_book
 * Starts refueling/recharging the 'Dartar' UAVs
 *
 * Arguments:
 * 0: Player <OBJECT>
 * 1: UAV <OBJECT>
 *
 * Return Value:
 * Nothing
 *
 * Example:
 * [player, theUAV] call ace_logistics_uavbattery_fnc_refuelUAV
 *
