    case (isNull findDisplay 12): {(findDisplay 12) displayCtrl 1090};
    case (isNull findDisplay 52): {(findDisplay 52) displayCtrl 1090};
    case (isNull findDisplay 53): {(findDisplay 53) displayCtrl 1090};
