 * None
 *
 * Public: No
 */

#include "script_component.hpp"

{
    _x params ["_time", "_objects"];
    if (CBA_missionTime - _time >= GVAR(litterCleanUpDelay)) then {
        {
            deleteVehicle _x;
        } forEach _objects;
        GVAR(allCreatedLitter) set[_forEachIndex, objNull];
    };
} forEach GVAR(allCreatedLitter);
