 * ReturnValue:
 * None
 *
 * Public: Yes
 */

#include "script_component.hpp"

params ["_medic", "_patient", "_items"];

private _itemsUsedBy = [];
{
    // handle a one of type use item
    if (_x isEqualType []) then {
        {
