TRACE_1("Attacking profile", _attackProfileName);

_attackProfilesCfg = ( configFile >> QGVAR(AttackProfiles) );

_attackProfile = nil;
for [{_i=0}, {_i< (count _attackProfilesCfg) }, {_i=_i+1}] do {
    _testProfile = _attackProfilesCfg select _i;
