/*
 * Author: commy2, esteldunedain
 * Draw the nametag and rank icon.
 *
 * Arguments:
 * 0: Unit (Player) <OBJECT>
 * 1: Target <OBJECT>
 * 2: Alpha <NUMBER>
 * 4: Height offset <NUMBER>
 * 5: Draw name <BOOL>
 * 5: Draw rank <BOOL>
 * 6: Draw soundwave <BOOL>
 *
 * Return Value:
 * None
 *
 * Example:
 * [ACE_player, bob, 0.5, height, true, true, true] call ace_nametags_fnc_drawNameTagIcon
 *
 * Public: No
 */

#include "script_component.hpp"

TRACE_1("drawName:", _this);

params ["", "_target", "", "_heightOffset"];

_fnc_parameters = {
    params ["_player", "_target", "_alpha", "_heightOffset", "_drawName", "_drawRank", "_drawSoundwave"];

    //Set Icon:
    private _icon = "";
    private _size = 0;
    if (_drawSoundwave) then {
        _icon = format [QPATHTOF(UI\soundwave%1.paa), floor random 10];
        _size = 1;
    } else {
        if (_drawRank && {rank _target != ""}) then {
            _icon = GVAR(factionRanks) getVariable (_target getVariable [QGVAR(faction), faction _target]);
            if (!isNil "_icon") then {
                _icon = _icon param [ALL_RANKS find rank _target, ""];
            } else {
                _icon = format ["\A3\Ui_f\data\GUI\Cfg\Ranks\%1_gs.paa", rank _target];
            };
            _size = 1;
        };
    };

    //Set Text:
    private _name = if (_drawName) then {
        [_target, true, true] call EFUNC(common,getName)
    } else {
        ""
    };

    //Set Color:
    private _color = [1, 1, 1, _alpha];
    if ((group _target) != (group _player)) then {
        _color = +GVAR(defaultNametagColor); //Make a copy, then multiply both alpha values (allows client to decrease alpha in settings)
        _color set [3, (_color select 3) * _alpha];
    } else {
        _color = [[1, 1, 1, _alpha], [1, 0, 0, _alpha], [0, 1, 0, _alpha], [0, 0, 1, _alpha], [1, 1, 0, _alpha]] select ((["MAIN", "RED", "GREEN", "BLUE", "YELLOW"] find ([assignedTeam _target] param [0, "MAIN"])) max 0);
    };

    private _scale = [0.333, 0.5, 0.666, 0.83333, 1] select GVAR(tagSize);

    [
        _icon,
        _color,
        [],
        (_size * _scale),
        (_size * _scale),
        0,
        _name,
        2,
        (0.05 * _scale),
        "RobotoCondensed"
    ]
};

private _parameters = [_this, _fnc_parameters, _target, QGVAR(drawParameters), 0.1] call EFUNC(common,cachedCall);
_parameters set [2, _target modelToWorldVisual ((_target selectionPosition "pilot") vectorAdd [0,0,(_heightOffset + .3)])];


drawIcon3D _parameters;

/*
 * Author: aeroson
 * Gathers and caches data needed by ace_nametags_fnc_doShow.
 * What really does make difference for the engine is simulation of CfgAmmo.
 * Priority of roles  is: driver/pilot, gunner, copilot, commander, FFV, cargo.
 *
 * Arguments:
 * None
 *
 * Return Value:
 * Data <ARRAY>
 * 0: Vehicle inherits from Air <BOOL>
 * 1: Categorized vehicle's turrets <ARRAY>
 *
 * Example:
 * call ace_nametags_fnc_updateSettings
 *
 * Public: No
 */
#include "script_component.hpp"
#include "common.hpp";

private ["_type", "_varName", "_data", "_isAir", "_config", "_fnc_addTurret", "_fnc_addTurretUnit"];

params ["_type"];

_varName = format ["ACE_CrewInfo_Cache_%1", _type];
_data = + (uiNamespace getVariable _varName);

if (!isNil "_data") exitWith {_data};

_data = [];
_isAir = _type isKindOf "Air";

_fnc_addTurretUnit = {
    private  ["_config", "_path", "_role", "_simulationEmpty", "_simulationLaserDesignate", "_simulationOther", "_magazine", "_ammo", "_simulation"];

    _config = _this select 0;
    _path = _this select 1;
    _role = CARGO;

    _simulationEmpty = 0;
    _simulationLaserDesignate = 0;
    _simulationOther = 0;
    {
        {
            _magazine = configFile >> "CfgMagazines" >> _x;
            _ammo = configFile >> "CfgAmmo" >> getText (_magazine >> "ammo");
            _simulation = getText (_ammo >> "simulation");

            if(_simulation=="") then {
                _simulationEmpty = _simulationEmpty + 1;
            } else {
                if(_simulation=="laserDesignate") then {
                    _simulationLaserDesignate = _simulationLaserDesignate + 1;
                } else {
                    _simulationOther = _simulationOther + 1;
                };
            };

        } forEach getArray (configFile >> "CfgWeapons" >> _x >> "magazines");
    } forEach getArray (_config >> "weapons");

    if(_simulationOther>0) then {
        _role = GUNNER;
    };
    if (_role == CARGO && {getNumber (_config >> "isCopilot") == 1}) then {
        _role = COPILOT;
    };
    if (_role == CARGO && {_simulationLaserDesignate>0 || getNumber (_config >> "primaryObserver") == 1}) then {
        _role = COMMANDER;
    };
    if (_role == CARGO && {getNumber (_config >> "isPersonTurret") == 1}) then {
        _role = FFV;
    };

    _data pushBack [_path, _role];  
};


_fnc_addTurret = {

    private ["_config", "_path", "_count", "_offset", "_index", "_turretPath", "_turretConfig"];

    _config = _this select 0;
    _path = _this select 1;

    _config = _config >> "Turrets";
    _count = count _config;

    _offset = 0;    

    for "_index" from 0 to (_count - 1) do {
        _turretPath = _path + [_index - _offset];
        _turretConfig = _config select _index;
        if (isClass _turretConfig) then {           
            [_turretConfig, _turretPath] call _fnc_addTurretUnit;
            [_turretConfig, _turretPath] call _fnc_addTurret;
        } else {
            _offset = _offset + 1;
        };
    };
};


_config = configFile >> "CfgVehicles" >> _type;
[_config, []] call _fnc_addTurret;

_data = [_isAir, _data];
uiNamespace setVariable [_varName, _data];

_data

/*
 * Author: Glowbal, PabstMirror
 * Starts up a PFEH to monitor the when players are talking.
 * Compatiblity with TFR/ACRE and Arma's VON
 *
 * Arguments:
 * None
 *
 * Return Value:
 * None
 *
 * Example:
 * [] call ace_nametags_fnc_initIsSpeaking
 *
 * Public: No
 */
#include "script_component.hpp"

if (isServer) then {
    //If someone disconnects while speaking, reset their variable
    addMissionEventHandler ["HandleDisconnect", {
        params ["_disconnectedPlayer"];
        if (_disconnectedPlayer getVariable [QGVAR(isSpeakingInGame), false]) then {
            _disconnectedPlayer setVariable [QGVAR(isSpeakingInGame), false, true];
        };
    }];
};

if (!hasInterface) exitWith {};

["unit", {
    //When player changes, make sure to reset old unit's variable
    params ["", "_oldUnit"];
    if ((!isNull _oldUnit) && {_oldUnit getVariable [QGVAR(isSpeakingInGame), false]}) then {
        _oldUnit setVariable [QGVAR(isSpeakingInGame), false, true];
    };
}] call CBA_fnc_addPlayerEventHandler;

if (isClass (configFile >> "CfgPatches" >> "acre_api")) then {
    ACE_LOGINFO("ACRE Detected.");
    DFUNC(isSpeaking) = {
        params ["_unit"];
        ([_unit] call acre_api_fnc_isSpeaking) && {!(_unit getVariable ["ACE_isUnconscious", false])}
    };
} else {
    if (isClass (configFile >> "CfgPatches" >> "task_force_radio")) then {
        ACE_LOGINFO("TFR Detected.");
        DFUNC(isSpeaking) =     {
            params ["_unit"];
            (_unit getVariable ["tf_isSpeaking", false]) && {!(_unit getVariable ["ACE_isUnconscious", false])}
        };
    } else {
        //No Radio Mod - Start a PFEH to watch the internal VON icon
        //Note: class RscDisplayVoiceChat {idd = 55} - only present when talking

        [{
            private ["_oldSetting", "_newSetting"];
            _oldSetting = ACE_player getVariable [QGVAR(isSpeakingInGame), false];
            _newSetting = (!(isNull findDisplay 55));
            if (!(_oldSetting isEqualTo _newSetting)) then {
                ACE_player setVariable [QGVAR(isSpeakingInGame), _newSetting, true];
            };
        } , 0.1, []] call CBA_fnc_addPerFrameHandler;

        DFUNC(isSpeaking) = {
            params ["_unit"];
            (_unit getVariable [QGVAR(isSpeakingInGame), false]) && {!(_unit getVariable ["ACE_isUnconscious", false])}
        };
    };
};

/*
 * Author: esteldunedain
 * Initializes the name tags module.
 *
 * Arguments:
 * Whatever the module provides.
 *
 * Return Value:
 * None
 */

#include "script_component.hpp"

if !(isServer) exitWith {};

params ["_logic", "", "_activated"];

if !(_activated) exitWith {};

GVAR(Module) = true;

[_logic, QGVAR(playerNamesViewDistance),  "playerNamesViewDistance"     ] call EFUNC(common,readSettingFromModule);
[_logic, QGVAR(showCursorTagForVehicles), "showCursorTagForVehicles"    ] call EFUNC(common,readSettingFromModule);

// Do Not Force - read module setting only non-default is set due to using SCALAR
if ((_logic getVariable "showPlayerNames") != -1) then {
    [_logic, QGVAR(showPlayerNames),      "showPlayerNames"             ] call EFUNC(common,readSettingFromModule);
};
if ((_logic getVariable "showNamesForAI") != -1) then {
