            if (_digit2 == 0) then {
                _digit1 = "";
                _digit2 = QPATHTOF(rsc\vector_minus.paa);
                _digit3 = format [QPATHTOF(rsc\vector_%1.paa), _digit3];
            } else {
                _digit1 = QPATHTOF(rsc\vector_minus.paa);
                _digit2 = format [QPATHTOF(rsc\vector_%1.paa), _digit2];
